SELECT a.id, a.orderNo, a.dealerId, a.deviceId, a.payType, a.outTradeNo, b.dealerName AS authCode, a.buyerId, a.cardNo, a.serviceType, a.payAmount, a.actualAmount, a.beforeAmount, a.afterAmount, c.deviceName AS subMerchantId, CASE a.STATUS WHEN '1' THEN '成功' WHEN '2' THEN '失败'
END STATUS, CASE a.payStatus WHEN '1' THEN '成功' WHEN '2' THEN '失败' WHEN '3' THEN '取消' END payStatus, a.payTime, a.creatTime, d.cardNo AS operatorId, c.deviceCode AS memo, a.planId, a.sendCmd, a.recieveCmd, CASE a.payType WHEN '1' THEN '支付宝' WHEN '2' THEN '微信' WHEN '3' THEN '预付' WHEN '4' THEN '现金' END hexSerial, CASE a.serviceType WHEN '1' THEN '预存' WHEN '2' THEN '现金' END AS hexDeviceCode, DATE_FORMAT(a.payTime, '%Y-%m-%d %H:%i:%s') AS updateStatus
FROM selterm.t_recharge a LEFT JOIN t_dealer b ON a.dealerId = b.id LEFT JOIN t_device c ON a.deviceId = c.id LEFT JOIN t_card d ON a.cardNo = d.id where a.id = #{id,jdbcType=INTEGER}

SELECT a.id, a.orderNo, a.dealerId, a.deviceId, a.payType, a.outTradeNo, b.dealerName AS authCode, a.buyerId, a.cardNo, a.serviceType, a.payAmount, a.actualAmount, a.beforeAmount, a.afterAmount, c.deviceName AS subMerchantId, CASE a.STATUS WHEN '1' THEN '成功' WHEN '2' THEN '失败'
END STATUS, CASE a.payStatus WHEN '1' THEN '成功' WHEN '2' THEN '失败' WHEN '3' THEN '取消' END payStatus, a.payTime, a.creatTime, a.operatorId, c.deviceCode AS memo, a.planId, d.cardNo AS sendCmd, a.recieveCmd, CASE a.payType WHEN '1' THEN '支付宝' WHEN '2' THEN '微信' WHEN '3' THEN '预付' WHEN '4' THEN '现金' END hexSerial, CASE a.serviceType WHEN '1' THEN '预存' WHEN '2' THEN '现金' END AS hexDeviceCode, DATE_FORMAT(a.payTime, '%Y-%m-%d %H:%i:%s') AS updateStatus FROM selterm.t_recharge a LEFT JOIN t_dealer b ON a.dealerId = b.id LEFT JOIN t_device c ON a.deviceId = c.id LEFT JOIN t_card d ON a.cardNo = d.id 1=1
and a.id=#{id,jdbcType=INTEGER}
and a.orderNo=#{orderNo,jdbcType=VARCHAR}
and a.payType=#{payType,jdbcType=INTEGER}
and b.dealerName like concat('%',#{authCode},'%')
and c.deviceName like concat('%',#{subMerchantId},'%')
and c.deviceCode =#{memo}
and a.serviceType=#{serviceType,jdbcType=INTEGER}
and a.status=#{status,jdbcType=VARCHAR}
and a.payStatus=#{payStatus,jdbcType=VARCHAR}
and a.payTime=#{payTime,jdbcType=TIMESTAMP}
and a.creatTime=#{creatTime,jdbcType=TIMESTAMP}
and a.operatorId=#{operatorId,jdbcType=VARCHAR}

	 select
  a.id,a.orderNo,a.dealerId,a.deviceId,a.payType,a.outTradeNo,a.authCode,a.buyerId,a.cardNo,a.serviceType,a.payAmount,a.actualAmount,
  a.beforeAmount,a.afterAmount,a.subMerchantId,a.status,a.payStatus,a.payTime,a.creatTime,a.operatorId,a.memo,a.planId,b.dealerName as sendCmd,a.recieveCmd
  from t_recharge a left join t_dealer b on a.dealerId= b.id
    1=1  


         and a.id=#{id,jdbcType=INTEGER}


         and orderNo=#{orderNo,jdbcType=VARCHAR}



         and a.payType=#{payType,jdbcType=INTEGER}


         and outTradeNo=#{outTradeNo,jdbcType=VARCHAR}


         and authCode=#{authCode,jdbcType=VARCHAR}


         and buyerId=#{buyerId,jdbcType=VARCHAR}


         and a.cardNo=#{cardNo,jdbcType=INTEGER}


         and a.serviceType=#{serviceType,jdbcType=INTEGER}



         and a.status=#{status,jdbcType=VARCHAR}


         and payStatus=#{payStatus,jdbcType=VARCHAR}

SELECT a.id, a.orderNo, a.dealerId, a.deviceId, a.payType, a.outTradeNo, b.dealerName AS authCode, a.buyerId, a.cardNo, a.serviceType, a.payAmount, a.actualAmount, a.beforeAmount, a.afterAmount, c.deviceName AS subMerchantId, CASE a.STATUS WHEN '1' THEN '成功' WHEN '2' THEN '失败'
END STATUS, CASE a.payStatus WHEN '1' THEN '成功' WHEN '2' THEN '失败' WHEN '3' THEN '取消' END payStatus, a.payTime, a.creatTime, a.operatorId, c.deviceCode AS memo, a.planId, d.cardNo AS sendCmd, a.recieveCmd, CASE a.payType WHEN '1' THEN '支付宝' WHEN '2' THEN '微信' WHEN '3' THEN '预付' WHEN '4' THEN '现金' END hexSerial, CASE a.serviceType WHEN '1' THEN '预存' WHEN '2' THEN '现金' END AS hexDeviceCode, DATE_FORMAT(a.payTime, '%Y-%m-%d %H:%i:%s') AS updateStatus FROM selterm.t_recharge a LEFT JOIN t_dealer b ON a.dealerId = b.id LEFT JOIN t_device c ON a.deviceId = c.id LEFT JOIN t_card d ON a.cardNo = d.id 1=1
and a.orderNo=#{params.orderNo}
and a.payType=#{params.payType}
and b.dealerName like concat('%',#{params.authCode},'%')
and c.deviceName like concat('%',#{params.subMerchantId},'%')
and c.deviceCode =#{params.memo}
and a.cardNo=#{params.cardNo}
and a.serviceType=#{params.serviceType}
and a.status=#{params.status}
and a.payStatus=#{params.payStatus}
and payTime >= DATE_FORMAT(#{params.payTimeStart},'%Y-%m-%d 00:00:00')
and payTime <= DATE_FORMAT(#{params.payTimeEnd},'%Y-%m-%d 23:59:59')
and creatTime >= DATE_FORMAT(#{params.creatTimeStart},'%Y-%m-%d 00:00:00')
and creatTime <= DATE_FORMAT(#{params.creatTimeEnd},'%Y-%m-%d 23:59:59')
and operatorId=#{params.operatorId}
